import React, { useEffect, useState } from "react";
import axios from "axios";

const Home = () => {
  const [tode, setTode] = useState([]);
  const [error, setError] = useState(null);
  const [loading, SetLoading] = useState(false);
  const [newtodo, Setnewtodo] = useState("");

  useEffect(() => {
    const featchdata = async () => {
      try {
        SetLoading(true);
        const response = await axios.get(
          "http://localhost:4000/api/todo/gettodos",
          {
            withCredentials: true,
            headers: {
              "Content-Type": "application/json",
            },
          }
        );
        console.log(response.data);
        setTode(response.data.pretodo);
        setError(null);
      } catch (error) {
        setError(error);
      } finally {
        SetLoading(false);
      }
    };
    featchdata();
  }, []);

  const TodoCreate = async () => {
    if (!TodoCreate) return;
    try {
      const response = await axios.post(
        "http://localhost:4000/api/todo/todocreate",
        {
          text: newtodo,
          completed: false,
        },
        {
          withCredentials: true,
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      console.log(response.data);
      setTode([...tode, response.data.newTodo]);
      setError(null);
      Setnewtodo("");
    } catch (error) {
      console.log(error);
      setError("Failed to Create Todo" || error);
    }
  };

  const TodoUpdate = async (id) => {
    const todolist = tode.find((t) => t._id === id);
    try {
      const response = await axios.put(
        `http://localhost:4000/api/todo/updatetodo/${id}`,
        {
          ...todolist,
          completed: !todolist.completed,
        },
        {
          withCredentials: true,
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      console.log(response.data);
      setTode(tode.map((t) => (t._id === id ? response.data : t)));
      setError(null);
    } catch (error) {
      console.log(error);
      setError("Failed to Create Todo" || error);
    }
  };

  const TodoDelete = async (id) => {
    try {
      const response = await axios.delete(
        `http://localhost:4000/api/todo/removetodo/${id}`,
        {
          withCredentials: true,
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      console.log(response.data);
      setTode(tode.filter((t) => t._id !== id));
      setError(null);
    } catch (error) {
      console.log(error);
      setError("Failed to Delete Todo" || error);
    }
  };

  return (
    <>
      <div className="bg-green-300 max-w-lg lg:max-w-lg rounded-lg shadow-lg mx-8 sm: mx-auto p-6 ">
        <h1 className="text-2xl  font-semibold text-center">Todo App</h1>
        <div className="flex mb-5 ">
          <input
            className="flex-grow p-2 border rounded focus:outline-none bg-slate-200"
            type="text"
            placeholder="Add a New Todo"
            value={newtodo}
            onChange={(e) => Setnewtodo(e.target.value)}
          />
          <button
            onClick={TodoCreate}
            className="bg-red-400 rounded text-white px-4 py-2 hover:bg-red-600 duration-300"
          >
            Add Todo
          </button>
        </div>
        <ul>
          <li className="flex items-center justify-between p-3 rounded-md">
            <div className="flex items-center">
              <input type="checkbox" className="mr-2 " />
              <span>Hello</span>
            </div>
            <button className="text-red-600 hover:text-red-900 duration-300">Delete</button>
          </li>
        </ul>
        <p className="mt-4 text-center text-sm  text-gray-500">0 Todo Remaning</p>
        <button className="mt-6 px-4 py-2 ">Logout</button>
      </div>
    </>
  );
};

export default Home;
