import React, { useEffect, useState } from "react";
import axios from "axios";

const Home = () => {
  const [tode, setTode] = useState([]);
  const [error, setError] = useState(null);
  const [loading, SetLoading] = useState(false);
  const [newtodo, Setnewtodo] = useState("")

  useEffect(() => {
    const featchdata = async () => {
      try {
        SetLoading(true);
        const response = await axios.get(
          "http://localhost:4000/api/todo/gettodos",
          {
            withCredentials: true,
            headers: {
              "Content-Type": "application/json",
            },
          }
        );
        console.log(response.data);
        setTode(response.data.pretodo);
        setError(null);
      } catch (error) {
        setError(error);
      } finally {
        SetLoading(false);
      }
    };
    featchdata();
  }, []);

  const TodoCreate = async () => {
    try {
      const response = await axios.post(
        "http://localhost:4000/api/todo/todocreate",

        {
          withCredentials: true,
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      console.log(response.data);
      setTode([...tode, response.data.newTodo]);
      setError(null);
    } catch (error) {
      console.log(error);
      setError("Failed to Create Todo" || error);
    }
  };

  return <div>Home</div>;
};

export default Home;
