import React, { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-hot-toast";

const Home = () => {
  const [tode, setTode] = useState([]);
  const [error, setError] = useState(null);
  const [loading, SetLoading] = useState(false);
  const [newtodo, Setnewtodo] = useState("");

  useEffect(() => {
    const featchdata = async () => {
      try {
        SetLoading(true);
        const response = await axios.get(
          "http://localhost:4000/api/todo/gettodos",
          {
            withCredentials: true,
            headers: {
              "Content-Type": "application/json",
            },
          }
        );
        console.log(response.data);
        setTode(response.data.pretodo);
        setError(null);
      } catch (error) {
        setError(error);
      } finally {
        SetLoading(false);
      }
    };
    featchdata();
  }, []);

  const TodoCreate = async () => {
    if (!TodoCreate) return;
    try {
      const response = await axios.post(
        "http://localhost:4000/api/todo/todocreate",
        {
          text: newtodo,
          completed: false,
        },
        {
          withCredentials: true,
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      console.log(response.data);
      setTode([...tode, response.data.newTodo]);
      setError(null);
      Setnewtodo("");
    } catch (error) {
      console.log(error);
      setError("Failed to Create Todo" || error);
    }
  };

  const TodoUpdate = async (id) => {
    const todolist = tode.find((t) => t._id === id);
    try {
      const response = await axios.put(
        `http://localhost:4000/api/todo/updatetodo/${id}`,
        {
          ...todolist,
          completed: !todolist.completed,
        },
        {
          withCredentials: true,
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      console.log(response.data);
      setTode(tode.map((t) => (t._id === id ? response.data : t)));
      setError(null);
    } catch (error) {
      console.log(error);
      setError("Failed to Create Todo" || error);
    }
  };

  const TodoDelete = async (id) => {
    try {
      const response = await axios.delete(
        `http://localhost:4000/api/todo/removetodo/${id}`,
        {
          withCredentials: true,
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      console.log(response.data);
      setTode(tode.filter((t) => t._id !== id));
      setError(null);
    } catch (error) {
      console.log(error);
      setError("Failed to Delete Todo" || error);
    }
  };

  return (
    <>
      <div className="container bg">
        <div className="row">
          <div className="col-md-6">
            <h1>Todo List</h1>
            {loading && <div>Loading...</div>}
            {error && <div>{error.message}</div>}
            <ul className="list-group">
              {tode.map((todo) => (
                <li key={todo._id} className="list-group-item">
                  {todo.text}
                  <div className="float-right">
                    <button
                      className={`btn btn-${
                        todo.completed ? "success" : "warning"
                      } btn-sm`}
                      onClick={() => TodoUpdate(todo._id)}
                    >
                      {todo.completed ? "Completed" : "Incomplete"}
                    </button>
                    <button
                      className="btn btn-danger btn-sm"
                      onClick={() => TodoDelete(todo._id)}
                    >
                      Delete
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
